class Containers:
    def __init__(self):
        self.containers = []

    def enqueue(self, package):
        for self.containers as c:
            if c.destination = package.destination:
                c.add(package)
                return;
        # New destination
        c = Container(package.destination)
        c.add(package)
        self.containers.append(c)

