class delayed:

    def __init__(self, seconds):
        self.seconds = seconds
        self.start = time()
        self.refused = False
        self.function = None
        self.args = None
        self.run_thread()

    def run_thread(self):

        def job():
            while True:
                sleep(self.seconds)
                if self.refused and self._time_ok():
                    self.function(*self.args)
                    self.refused = False

        thread = Thread(target=job)
        thread.daemon = True
        thread.start()

    def _time_ok(self):
        return time() - self.start > self.seconds

    def __call__(self, function):
        self.function = function

        def decorated(*args):
            self.args = args

            if self._time_ok():
                self.start = time()
                function(*self.args)
            else:
                self.refused = True

        return decorated


